import {Component} from 'react'

import Api from '../api/axios'

import Login from '../components/login'
import Screen from '../components/screen'


class Index extends Component{


    _HandleSignUp = async() => {
        try {
            const result = await Api.post(`/user/login`, this.state)
            Api.AuthorizationHeader(result.data.token)
        } catch (e){
            console.log(e)
        }
    }


    _HandleGetMe = async() => {
        try {
            const result = await Api.get(`/user/me`)
            console.log(result)
        } catch (e){
            console.log(e)
        }
    }


    render() {

        return(

            <div className="d-flex">

                <div style={{flex:1}}>
                    <Login/>
                </div>

                <div style={{flex:1}}>
                    <Screen/>
                </div>
            </div>

        )
    }
} 

export default Index;
