import {Component} from 'react'

import Api from '../../api/axios'

import Login from './components/Login'
import SignUp from './components/SignUp'


class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            stage : 0,
            loading: false
        }
    }


    _renderForm = () => {
        switch(this.state.stage){
            case 0:
                return (

                    <Login
                        handleStage={() => this.setState({stage: 1})}
                        _handleLogin={this._handleLogin}
                        loading={this.state.loading}
                    />
                )
            case 1 :
                return (
                    <SignUp
                        handleStage={() => this.setState({stage: 0})}
                        _handleSignUp={this._handleSignUp}
                        loading={this.state.loading}
                    />
                )
            default:return
        }
    }

    _handleLogin = async(user) => {
        try {
            const result = await Api.post(`/user/login`, user)
            console.log(result.data)
            // Api.AuthorizationHeader(result.data.token)
        } catch (e){
            console.log('error')
            console.log(typeof(e))
        }
    }

    _handleSignUp= async(user) => {
        try {
            const result = await Api.post(`/user/signup`, user)
            // Api.AuthorizationHeader(result.data.token)
        } catch (e){
            console.log(e)
        }
    }

    _HandleGetMe = async() => {
        try {
            const result = await Api.get(`/user/me`)
            console.log(result)
        } catch (e){
            console.log(e)
        }
    }



    render() {

        return (
            <div className="d-flex justify-content-between " style={{flexDirection:'column', padding: 50, height:'100%'}}>

                <div className="d-flex justify-content-between">
                    <img src="/static/logo.png" style={{objectFit:'contain', height:25}}/>
                    <span>Home</span>
                </div>




                <div style={{display:"flex", justifyContent:"center", flexDirection:'column', alignItems:"center"}}>
                    <div style={{minWidth: 310, maxWidth: 410}}>
                        {this._renderForm()}
                    </div>
                </div>


                <div className="d-flex justify-content-between">
                    <span>About Us | Privacy</span>
                    <span>FR | EN</span>
                </div>

            </div>
        );

    }
} 

export default Index;
