import React, {Component} from 'react'

import Api from '../../../api/axios'

import Input from '../../form/Input'
import Button from '../../form/Button'


class Login extends Component  {

    constructor(props) {
        super(props);
        this.state = {

            form: {
                email: '',
                password: '',
            },
            error: null,
        }
    }

    _HandleChange = (e, element) => {
        let form = {...this.state.form}
        form[element] = e
        this.setState({form: form, error: null})
    }


    _handleLogin = async() => {

        this.setState({loading: true})
        try {
            const result = await Api.post(`/user/login`, this.state.form)
            console.log(result.data)
            // Api.AuthorizationHeader(result.data.token)
        } catch (e){
            this.setState({error: e.response.data.errors, loading: false})
        }
    }

    render() {


        const {email, password} = this.state.form

        return (
            <div style={{flex: 1}}>

                {this.state.loading && 
                <>
                    <div>
                        <span>Harness the power of big data</span>
                        <h4>Login your account</h4>
                    </div>

                    <Input
                        divStyle={{width: '100%', marginRight: 30}}
                        title='E-Mail'
                        placeholder="Your E-Mail"
                        value={email}
                        element={'email'}
                        _HandleProduct={this._HandleChange}

                    /> 

                    <Input
                        divStyle={{width: '100%', marginRight: 30}}
                        title={'Password'}
                        placeholder="Your Password"
                        value={password}
                        element={'password'}
                        _HandleProduct={this._HandleChange}
                    />  

                    <div className="d-flex justify-content-between" style={{marginTop: 15}}>

                        <Button
                            _Function={this.props.handleStage}
                            title={'Register'}
                            style={{}}
                        />

                        <Button
                            _Function={this._handleLogin}
                            title={'Login'}
                            style={{color:'white',border:'1px solid rgba(38,162,192,1)', backgroundColor: 'rgba(38,162,192,1)'}}
                        />
                    </div>

                    {this.state.error && 
                        <div className="d-flex flex-column" style={{marginTop: 5}}>
                            <span style={{fontSize: 12, }}>{this.state.error}</span>
                        </div>
                    }
                </>
                }
            </div>
        )
    };
}
export default Login;
