        }
    }

    render() {

        return(

            <div style={{flex: 1, display:"flex", height: '100%'}}>

                <Home

                />

            </div>

        )
    }
} 


const mapStateToProps = state => {
    return {}
}

export default connect(mapStateToProps,{handleAccountLogout_success})(Index)
