import {Component} from 'react'


import Login from './components/Login'
import SignUp from './components/SignUp'


class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            stage : 0
        }
    }


    _renderForm = () => {
        switch(this.state.stage){
            case 0:
                return (
                    <div>

                        <Login
                            handleStage={() => this.setState({stage: 1})}
                        />

                    </div>
                )
            case 1 :
                return (
                    <div>

                        <SignUp
                            handleStage={() => this.setState({stage: 0})}

                        />

                        {/* <button onClick={() => this.setState({stage: 0})}>Login Here</button> */}

                    </div>
                )
            default:return
        }
    }


    render() {

        return (
            <div style={{flex: 1, display:'flex', flexDirection:'column', padding: 25}}>

                <div>
                    logo || home
                </div>

                {this._renderForm()}

                <div>About Us | Privacy</div>

            </div>
        );

    }
} 

export default Index;
