import {Component} from 'react'





class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            stage : 0
        }
      }




    render() {

        return (
            <div style={{flex: 1, display:'flex', flexDirection:'column'}}>

                <div>
                    logo || home
                </div>


                {this.state.stage && 
                    <div>
                        Actual Sign Up Page
                    </div>
                }



                <div>Footer Info</div>

            </div>
        );

    }
} 

export default Index;
