import {Component} from 'react'


import Login from './components/Login'
import SignUp from './components/SignUp'


class Index extends Component {

    constructor(props) {
        super(props);
        this.state = {
            stage : 0
        }
    }


    _renderForm = () => {
        switch(this.state.stage){
            case 0:
                return (

                    <Login
                        handleStage={() => this.setState({stage: 1})}
                    />
                )
            case 1 :
                return (
                    <SignUp
                        handleStage={() => this.setState({stage: 0})}
                    />
                )
            default:return
        }
    }


    render() {

        return (
            <div className="d-flex justify-content-between " style={{flexDirection:'column', padding: 30, minWidth: 310}}>

                <div className="d-flex justify-content-between">
                    <img src="/static/logo.png" style={{objectFit:'contain', height:25}}/>
                    <span>Home</span>
                </div>

                {this._renderForm()}


                <div className="d-flex justify-content-between">
                    <span>About Us | Privacy</span>
                    <span>FR | EN</span>
                </div>

            </div>
        );

    }
} 

export default Index;
