import {Component} from 'react'

import Api from '../../../api/axios'

import Input from '../../form/Input'
import Button from '../../form/Button'
import StaticName from '../../form/StaticName'


class Login extends Component  {

    constructor(props) {
        super(props);
        this.state = {

            form: {
                name: '',
                email: '',
                password: '',
            }

        }
    }

    _HandleChange = (e, element) => {
        let form = {...this.state.form}
        form[element] = e
        this.setState({form: form})
    }

    _handleSignUp= async(user) => {
        try {
            const result = await Api.post(`/user/signup`, user)
            // Api.AuthorizationHeader(result.data.token)
        } catch (e){
            console.log(e)
        }
    }

    render() {


        const {name, email, password} = this.state.form

        return (
            <div style={{flex: 1}}>

                <div className="d-flex flex-column">
                    <StaticName title="DISCOVERING THE NEW TREND IN YOUR SHOPPERS" style={{fontSize: 10, fontWeight: '600', color:'rgba(0,0,0,0.5)'}}/>
                    <StaticName title="Sign up for account" style={{fontSize: 18, fontWeight: '600', color:'rgba(0,0,0,1)', marginTop: 5, marginBottom:10}}/>
                </div>

                <Input
                    divStyle={{width: '100%', marginRight: 30}}
                    title='Name'
                    placeholder="Your name"
                    value={name}
                    element={'name'}
                    _HandleProduct={this._HandleChange}

                /> 

                <Input
                    divStyle={{width: '100%', marginRight: 30}}
                    title='E-Mail'
                    placeholder="Your E-Mail"
                    value={email}
                    element={'email'}
                    _HandleProduct={this._HandleChange}

                /> 

                <Input
                    divStyle={{width: '100%', marginRight: 30}}
                    title={'Password'}
                    placeholder="Your Password"
                    value={password}
                    element={'password'}
                    _HandleProduct={this._HandleChange}
                />  

                <div className="d-flex justify-content-between" style={{marginTop: 15}}>

                    <Button
                        _Function={this.props.handleStage}
                        title={'Login'}
                        style={{border: '1px solid rgba(38,162,192,1)', color: 'rgba(38,162,192,1)'}}
                    />

                    <Button
                        _Function={() => console.log('sign up')}
                        title={'Sign Up'}
                        style={{
                            color:'white',border:'1px solid rgba(38,162,192,1)', backgroundColor: 'rgba(38,162,192,1)',
                            boxShadow: '0 5px 9px 0 rgba(38,162,192,0.35), 0 8px 25px 0 rgba(38,162,192,0.35)'
                        }}
                    />
                </div>


            </div>
        )
    };
}
export default Login;
